import React from 'react';
import { render, cleanup } from '@testing-library/react';
import Title from './Title.jsx';

// Test suite for Title component
describe('Title component', () => {

  // Clean up after each test case
  afterEach(cleanup);

  test('renders without crashing', () => {
    const { container } = render(<Title title="Test title" />);
    expect(container).toBeDefined();
  });

  test('displays title prop within an h4 tag', () => {
    const testTitle = 'Test title';
    const { getByText } = render(<Title title={testTitle} />);
    const titleElement = getByText(testTitle);
    expect(titleElement).toBeDefined();
    expect(titleElement.tagName).toBe('H4');
  });

  test('does not display title when prop is an empty string', () => {
    const { container } = render(<Title title="" />);
    expect(container.querySelector('h4')).not.toHaveTextContent();
  });

  test('does not break when title prop is not a string', () => {
    const { getByText } = render(<Title title={{}} />);
    expect(getByText('[object Object]')).toBeDefined();
  });

  // Add more cases as needed for different scenarios...
});

